-- Detect annotations which sentence has been deleted.SELECT sa.sentence_idFROM sentence_annotations sa  LEFT JOIN sentences s ON sa.sentence_id = s.idWHERE s.id IS NULL;-- Detect annotations with meaning_id mismatchSELECT sa.sentence_id, sa.meaning_idFROM sentence_annotations saWHERE sa.meaning_id > 0AND (sa.sentence_id, sa.meaning_id) NOT IN (  SELECT st.sentence_id, st.translation_id  FROM sentences_translations st);-- Detect where meaning_id is not an English sentence...SELECT sa.sentence_id, sa.meaning_idFROM sentence_annotations sa  JOIN sentences s ON sa.meaning_id = s.idWHERE sa.meaning_id > 0AND s.lang != 'eng';