---- Table structure for table `private_messages`---- This table stores the private messages.-- TODO for Etienne--CREATE TABLE IF NOT EXISTS `private_messages` (  `id` int(11) NOT NULL auto_increment,  `recpt` int(11) NOT NULL,  `sender` int(11) NOT NULL,  `user_id` int(11) NOT NULL default '0',  `date` datetime NOT NULL,  `folder` enum('Inbox','Sent','Trash') character set utf8 NOT NULL default 'Inbox',  `title` varchar(255) character set utf8 NOT NULL,  `content` text character set utf8 NOT NULL,  `isnonread` tinyint(4) NOT NULL default '1',  PRIMARY KEY  (`id`),  KEY `idx_recpt` (`recpt`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=245 ;